INSERT INTO `case_main` (`case_id`,`title`,`overview`,`created_date`,`publication_date`,`case_text`,`abstract_text`,`additional_information`,`current`,`archived`,`rating`,`development_status`,`development_status_notes`,`display_status`,`available_cme_credits`,`cme_release_date`,`cme_valid_until`,`number_cme_credits_available`,`tag_line`) VALUES (1,'Hepatitis B','A 28yo Asian woman presents to arrange ongoing care for her new baby following delivery. She is in her 37th week of what has been a completely uneventful, normal, first pregnancy. She has had excellent prenatal care and fetal development has been normal based on standard, non-invasive measures.','0000-00-00 00:00:00','2015-08-20 00:00:00','A 28yo Asian woman presents to arrange ongoing care for her new baby following delivery. She is in her 37th week of what has been a completely uneventful, normal, first pregnancy. She has had excellent prenatal care and fetal development has been normal based on standard, non-invasive measures. ','The current recommendations from the Advisory Committee on Immunization Practices (ACIP) for infants born to hepatitis B-infected mothers include postexposure prophylaxis consisting of hepatitis B (HepB) vaccine and hepatitis B immune globulin administered within 12 hours of birth, followed by completion of the 3- or 4-dose HepB vaccine series (1). To identify infants who need revaccination as well as those who need follow-up medical care for hepatitis B virus (HBV) infection, ACIP currently recommends HepB post-vaccination serologic testing (PVST) at age 9–18 months (1). This report provides CDC guidance for shortening the interval for PVST to age 9–12 months to reduce the need for unnecessary revaccination and was prompted by new data from the Enhanced Perinatal Hepatitis B Prevention Program (EPHBPP).','',1,0,4,5,NULL,0,1,NULL,NULL,3,NULL);
INSERT INTO `case_main` (`case_id`,`title`,`overview`,`created_date`,`publication_date`,`case_text`,`abstract_text`,`additional_information`,`current`,`archived`,`rating`,`development_status`,`development_status_notes`,`display_status`,`available_cme_credits`,`cme_release_date`,`cme_valid_until`,`number_cme_credits_available`,`tag_line`) VALUES (2,'Sodium Intake','A 36yo white male comes in for a new patient visit in order to establish care. He and his wife recently moved to your community from a suburb of Milwaukee where they were under the care of a residency classmate of yours. He has no specific complaints and no prior major illnesses or surgeries. A careful review of his records suggests that he is up to date on all recommended clinical preventive services. Your colleague recently administered the TD vaccine and screened him for hypertension and cholesterol abnormalities. He is in excellent physical shape and reports being physically active, running 4-5 times a week. He has a BMI of 21.  ','0000-00-00 00:00:00','2015-08-20 00:00:00','A 36yo white male comes in for a new patient visit in order to establish care. He and his wife recently moved to your community from a suburb of Milwaukee where they were under the care of a residency classmate of yours. He has no specific complaints and no prior major illnesses or surgeries. A careful review of his records suggests that he is up to date on all recommended clinical preventive services. Your colleague recently administered the TD vaccine and screened him for hypertension and cholesterol abnormalities. He is in excellent physical shape and reports being physically active, running 4-5 times a week. He has a BMI of 21.  ','What is already known on this topic?',NULL,0,0,NULL,5,NULL,2,NULL,NULL,NULL,4,'A 36yo white male comes in for a new patient visit in order to establish care. ');
